package Ch_5_LoopControlInstrcution;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * do {
		 * 
		 * //code
		 * 
		 * } while (condition); //Note this semicolon
		 */
		int i = 1;
		do {
			System.out.println(i);
			i++;
		} while (i <= 10);
	}

}
