package Ch_6_Arrays;

public class ArrayTraversal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// declaring an array
		int arr[] = { 1, 2, 3, 3, 4, 5 };
		// traversing the array with for-each loop
		for (int i : arr) {
			System.out.println(i);
		}
	}
}